import React, { Component } from 'react'
import './TodoList.css'
import Tag from './Tag'

class TodoList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            completed: this.props.todo_item.completed
        }
    }

    handleClick = () => { this.props.onClick(this.props.todo_item.id) }

    handleDelete = () => { this.props.onDelete(this.props.todo_item.id) }

    handleComplete = () => {
        if (this.state.completed) {
            return "completed"
        }
        return "incomplete"
    }

    handleChange = (e) => { 
        this.props.todo_item.completed = !this.props.todo_item.completed
        this.state.completed = this.props.todo_item.completed
        this.props.update(this.props.todo_item) 
    }

    render() {
        return (
            <div className="todo_items">
                <div className="list" id={this.props.todo_item.id} key={this.props.todo_item.id} >
                    <p onClick={this.handleClick} className={""+this.handleComplete} >
                        <input type="checkbox" onChange={this.handleChange} />
                        {this.props.todo_item.content}
                        <i className="fa fa-trash" aria-hidden="true" onClick={this.handleDelete}></i>
                    </p>
                    <Tag tags={this.props.todo_item.list_of_tags} />
                </div>
            </div>
            /*
            <div className="col-md-8">
                <div className="pt-4 pb-4">
                    <h4 onClick={this.handleClick}>{this.props.todo_item.content}</h4>
                    <p onClick={this.handleClick}>{this.props.todo_item.list_of_tags}</p>
                    <div className="cta-wrapper">
                        <a href={"/todo_lists/" + this.props.todo_item.id}>
                            <button type="button" className="btn-outline-primary btn-rounded waves-effect btn cta-btn">
                                View Todo List <i className="fa fa-eye"></i>
                            </button>
                        </a>
                        <button type="button" className="btn-outline-primary btn-rounded waves-effect btn cta-btn">
                            Edit <i className="fa fa-pencil"></i>
                        </button>
                        <button type="button"
                            className="btn-outline-primary btn-rounded waves-effect btn cta-btn"
                            onClick={this.handleDelete}>
                            Delete <i className="fa fa-trash"></i>
                        </button>
                    </div>
                </div>
            </div>
            */
        )
    }
}

export default TodoList
