import React, {Component} from 'react'
import axios from 'axios'
import update from 'immutability-helper'

import Table from './Table/Table'

class Home extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todo_lists: []
        };
    }
    componentDidMount() {
        axios.get('http://localhost:3001/api/v1/todo_lists.json')
            .then(response => {
                console.log(response)
                this.setState({ todo_lists: response.data })
            })
            .catch(error => console.log(error))
    }
    addNewTodoList = item => {
        axios.post(
            'http://localhost:3001/api/v1/todo_lists',
            item
        )
            .then(response => {
                console.log(response)
                const todo_lists = update(this.state.todo_lists, {
                    $splice: [[0, 0, response.data]]
                })
                this.setState({ todo_lists: todo_lists })
            })
            .catch(error => console.log(error.response.data))
    }
    render() {
        return (
            <div>
                <button className="new-todo-list-btn" onClick={this.addNewTodoList({
                    todo_list: { title: '', description: '', }})}>
                    New Todo List
                </button>
                <Table todo_lists = {this.state.todo_lists}/>
            </div>
        )
    }
}

export default Home
