import React, {Component} from 'react'
import axios from 'axios'

import Table from './Table/Table'

class Home extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todo_lists: []
        };
    }
    componentDidMount() {
        axios.get('http://localhost:3001/api/v1/todo_lists.json')
            .then(response => {
                console.log(response)
                this.setState({ todo_lists: response.data })
            })
            .catch(error => console.log(error))
    }
    addNewTodoList = () => {
        axios.post(
            'http://localhost:3001/api/v1/todo_lists',
            {title: '', description: ''}
        )
            .then(response => {
                console.log(response)
            })
            .catch(error => console.log(error.response))
    }
    render() {
        return (
            <div>
                <button className="new-todo-list-btn" onClick={this.addNewTodoList}>
                    New Todo List
                </button>
                <Table todo_lists = {this.state.todo_lists}/>
            </div>
        )
    }
}

export default Home
