import React, { Component } from 'react'
import axios from 'axios'

class TodoItemForm extends Component {
    constructor(props) {
        super(props)
        this.state = {
            content: this.props.todo_item.content,
            list_of_tags: this.props.todo_item.list_of_tags
        }
    }

    handleInput = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    handleBlur = () => {
        const todo_item = { content: this.state.content, list_of_tags: this.state.list_of_tags}
        axios.put(
            `http://localhost:3001/api/v1/todo_lists/${this.props.todo_item.todo_list_id}/todo_items/${this.props.todo_item.id}`,
            { todo_item: todo_item }
        )
            .then(response => {
                console.log(response)
                this.props.updateTodoItem(response.data)
            })
            .catch(error => console.log(error.response))
    }

    render() {
        return (
            <div className="tile">
                <form onBlur={this.handleBlur} >
                    <input className='input' type="text" name="content" placeholder='Enter a Task'
                        value={this.state.content} onChange={this.handleInput}
                        ref={this.props.titleRef} />
                    <textarea className='input' name="list_of_tags" placeholder='Enter tags'
                        value={this.state.list_of_tags} onChange={this.handleInput}></textarea>
                </form>
            </div>
        );
    }
}

export default TodoItemForm
